//import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


const App = () => {


  const [color, setcolor] = useState("")

  const mystyle = {
    color: "black",
    backgroundColor: "color",
    textAlign: "center",
    padding: "10px",
    fontFamily: "Arial",
    height: "20px",
    width: "150px"
  };


  return (
    <div className="container">
      <div className="mb-3" style={{ textAlign: "center", height: "150px", width: "165px" }}>
      <span className="square border border-primary"></span>

      </div>
      <br />
      <div style={{ textAlign: "center" }}>
        <input style={mystyle} type="text" value={color} onChange={(e) => setcolor(e.target)} className="form-control" id="ex" placeholder="Enter" />
      </div>

    </div>

  );
}

export default App;
