//import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';


const App = () => {


  const [color, setcolor] = useState("")



  return (
    <div align="center">

      <br />
      <div style={{ backgroundColor: "color", height:"50px" }}>
        <input  type="text" value={color} onChange={(e) => setcolor(e.target)}  />
      </div>

    </div>

  );
}

export default App;
